mapper.select:
SELECT MEMBER_ID, NAME, ID, AUTH
FROM MEMBER
WHERE ID=#{id} AND PASSWORD =#{password}

SELECT MEMBER_ID, NAME, ID, PASSWORD, TEL, AUTH
FROM MEMBER
WHERE MEMBER_ID =#{member_id}

mapper.insert:
UPDATE MEMBER
PASSWORD =#{password}, TEL =#{tel},
WHERE MEMBER_ID =#{member_id}

mapper.select:
SELECT ID
FROM MEMBER
WHERE ID =#{id}
